import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import profileIcon from '../assets/user_profile.png';
import '../css/TopNav.css';

const TopNav = (props) => {
    const {userEmail} = props;
    const { logout, userName } = useAuth();
    const navigate = useNavigate();

    const goToProfile = () => {
        //navigate(`/profile/{state:{email:props.userEmail}}`);
        navigate(`/profile/${props.userEmail}`,{state:{email:props.userEmail}});
    };

    return (
        <nav className="top-nav">
            <span className="user-name">Hello, {userName}!</span>
            <div className="profile-section">
                <img src={profileIcon} alt="Profile" className="profile-icon" onClick={goToProfile} />
                <button onClick={logout} className="logout-button">Logout</button>
            </div>
        </nav>
    );
}

export default TopNav;
