import React, {useState} from "react";
import axios from 'axios';

function Login() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [signedIn, setSignedIn] = useState(false);
    const [resp, setResp] = useState({});
    const [error, setError] = useState('');

    const handleChange = (e) => {
        if (e.target.name === "name") {
            setName(e.target.value);
        } else if (e.target.name === "email") {
            setEmail(e.target.value);
        } else if (e.target.name === "password") {
            setPassword(e.target.value);
        }
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log(name, email, password);

        try {
            //const response = await axios('http://localhost:8080/welcome');
            //console.log(response.data);
            //setResp(response);

            const userData = {
                userName: name,
                userEmail: email,
                userPassword: password
            }

            axios.post('http://localhost:8080/login', userData)
            .then((response) => {
                console.log(response);
            })
            .catch((error) => {
                console.log('The Error: ', error);
                setError(error);
            });
        } catch (e) {
            console.log(e);
        }
    }

    return (
        <div className="signup-container">
            <div className="signup-header">
                <h1>Sign Up for an Account</h1>
                <h5>Already have an account? <a href="/login">Login</a></h5>
            </div>

            <div className="signup-body">
                <form action="">
                    <div className="signup-name">
                        <label for="name">Name: </label>
                        <input type="text" id="name" name="name" placeholder="Name" value={name} onChange={e => handleChange(e)} required></input>
                    </div>

                    <br></br>

                    <div className="signup-email">
                        <label for="emailAddress">Email: </label>
                        <input type="email" id="emailAddress" name="email" placeholder="Email" value={email} onChange={e => handleChange(e)} required></input>
                    </div>

                    <br></br>

                    <div className="signup-pwd">
                        <label for="password">Password: </label>
                        <input type="password" id="password" name="password" placeholder="Password" value={password} onChange={e => handleChange(e)} required></input>
                    </div>

                    <br></br>

                    <div className="signup-submit-button">
                        <button id="submit-button" onClick={handleSubmit}><b>Sign Up</b></button>
                        { /* <Button submit={handleSubmit} userName={name} /> */ }
                    </div>

                    {resp && <p id="welcome">{resp.data}</p> }
                    {/* error && <p>{error}</p> */}
                </form>
            </div>
        </div>
    );
}

export default Login;
